SELECT "URLHash", CAST(CAST("EventDate" AS INT) AS DATE), COUNT(*) AS PageViews FROM hits WHERE "CounterID" = 62 AND CAST(CAST("EventDate" AS INT) AS DATE) >= '2013-07-01' AND CAST(CAST("EventDate" AS INT) AS DATE) <= '2013-07-31' AND "IsRefresh" = 0 AND "TraficSourceID" IN (-1, 6) AND "RefererHash" = 3594120000172545465 GROUP BY "URLHash", CAST(CAST("EventDate" AS INT) AS DATE) ORDER BY PageViews DESC LIMIT 10 OFFSET 100;
